import React, {Component} from 'react';
import { BrowserRouter, Route, NavLink, Switch, Link } from "react-router-dom";
import MainPage from './components/MainPage';
import LoginForm from './components/LoginForm';
import Cookie from './components/functions/Cookie';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user_id: 0,
      user_logged_id: 0,
    }
  }

  componentWillMount() {
    const cookie = new Cookie();
    const cookie_id = cookie.getCookie('user_id');
    this.setState({ user_id: parseInt(cookie_id), user_logged_id: parseInt(cookie_id) });
  }

  componentDidMount() {
    if (window.location.pathname.includes('id')) {
      const current_id = window.location.pathname.slice(3);
      this.setState({ user_id: parseInt(current_id)});
    }
  }

  render() {
    const { user_id, user_logged_id } = this.state;

    return (
      <BrowserRouter>
        <Switch>
          <Route exact path={"/id:user_id"} render={ ()=> 
            <div className="container">
              <MainPage 
                user_id={user_id}
                user_logged_id={user_logged_id}  
              />
            </div>
          }/>
          <Route exact path="/login" render={ ()=> 
            <LoginForm />
          }/>
        </Switch> 
      </BrowserRouter> 
    );
  }
}

export default App;
