import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class NavFriends extends Component {
  constructor(props) {
    super(props);
    this.state = {
      friends: [],
    }
  }

  componentDidMount() {
    fetch(`http://akvatory.local/api/user/read.php`)
      .then(response => response.json())
      .then(friends => this.setState({ friends: friends.records }))
      .then(console.log(this.state.friends))
      
  }

  render() {
    return (
      <ul onClick={console.log(this.state.friends)}>
      {this.state.friends.map((friend, i) => {                    
          return (
            <Link to={`id${friend.id}`}>
              <li key={i}>{friend.username}</li>
            </Link>
          ) 
        })} 
      </ul>
    );
  }
}

export default NavFriends;
