import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import './css/Navigation.css';

class Navigation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sections: [
        {
          section: 'my',
          alt: 'Моя страница',
          img: 'my-page.png',
        },
        {
          section: 'messages',
          alt: 'Сообщения',
          img: 'messages.png',
        }
      ]
    }
  }


  render() {
    return (
      <div className="navigation-container">
        {this.state.sections.map((section, i) => {                    
          return (
            <Link key={i}
              to={`/id${this.props.user_logged_id}`} 
              className={this.props.user_id === this.props.user_logged_id && this.props.section == section.section ? 'disabled-link' : ''}
              onClick={this.props.handleChangeUrl}
            >
              <div 
                onClick={() => this.props.handleChangeSection(section.section)} 
                className={this.props.user_id === this.props.user_logged_id && this.props.section == section.section ? 'navigation-item active' : 'navigation-item'}
              >
                <img src={window.location.origin + `/img/icons/${section.img}`} alt={section.alt} title={section.alt} />
                <p>{section.alt}</p>
              </div>
            </Link>

          ) 
        })}

        <Link 
          to={`/id${this.props.user_logged_id}`} 
          className={this.props.user_id === this.props.user_logged_id && this.props.section == 'my' ? 'disabled-link' : ''}
          onClick={this.props.handleChangeUrl}
        >
          <div 
            onClick={() => this.props.handleChangeSection('my')} 
            className={this.props.user_id === this.props.user_logged_id && this.props.section == 'my' ? 'navigation-item active' : 'navigation-item'}
          >
            <img src={window.location.origin + '/img/icons/my-page.png'} alt="Моя страница" title="Моя страница" />
            <p>Моя страница</p>
          </div>
        </Link>

        <div 
          onClick={() => this.props.handleChangeSection('messages')} 
          className={this.props.section == 'messages' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/messages.png'} alt="Сообщения" title="Сообщения" />
          <p>Сообщения</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('news')} 
          className={this.props.section == 'news' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/rupor.png'} alt="Новости компании" title="Новости компании" />
          <p>Новости компании</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('tasks')} 
          className={this.props.section == 'tasks' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/tasks.png'} alt="Входящие задачи" title="Входящие задачи"></img>
          <p>Входящие задачи</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('colleagues')} 
          className={this.props.section == 'colleagues' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/friends.png'} alt="Коллеги" title="Коллеги"></img>
          <p>Коллеги</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('learnings')} 
          className={this.props.section == 'learnings' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/learning.png'} alt="Обучение" title="Обучение"></img>
          <p>Обучение</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('gallery')} 
          className={this.props.section == 'gallery' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/gallery.png'} alt="Галерея" title="Галерея"></img>
          <p>Галерея</p>
        </div>
        <div 
          onClick={() => this.props.handleChangeSection('favourites')} 
          className={this.props.section == 'favourites' ? 'navigation-item active' : 'navigation-item'}
        >
          <img src={window.location.origin + '/img/icons/favourites.png'} alt="Избранное" title="Избранное"></img>
          <p>Избранное</p>
        </div>
      </div>
    )
  };

}

export default Navigation;
