import React, {Component} from 'react';
import './css/MainpageHeader.css';
import { url } from 'inspector';

class MainpageHeader extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: [],
      noavatar: true,
    }
  }

  componentDidMount() {
    fetch('http://akvatory.local/api/user/read_one.php?id=17')
      .then(response => response.json())
      .then(user => this.setState({ user }));
  }

  render() {
    const { user } = this.state;
    const defaultAvatar = { backgroundImage: './img/photos/images.png'};

    return (
      <section>
        <header className="header">
          <div className="person">
            <div className="person-image" style={{ background: `url('./img/photos/images.png')`}}></div>
            <div className="person-info">
              <div className="person-info__container">
                <p className="person-info__name">{user.username}</p>
                <p className="person-info__position">{user.position}</p>
              </div>
              <a href="logout.php"><button className="btn person-logout">Выйти</button></a>
            </div>
          </div>
          <button className="btn upload-cover">Загрузить обложку</button>
        </header>
       
      </section>
    )
  }
}

export default MainpageHeader;
